import React, { useState, useEffect, useRef } from 'react';

function AutocompleteSearch() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [open, setOpen] = useState(false);
  const [history, setHistory] = useState([]);
  const inputRef = useRef(null);

  const handleChange = (event) => {
    setQuery(event.target.value);
    setOpen(true);
  };

  const handleSelect = (suggestion) => {
    setQuery(suggestion);
    setResults([]);
    setOpen(false);
    setHistory([...history, suggestion]);
  };

  useEffect(() => {
    if (inputRef.current === document.activeElement) {
      setResults(history);
      setOpen(true);
    }
  }, [history]);

  useEffect(() => {
    if (query.length > 0) {
      // retrieve suggestions from API
      fetch(`http://localhost:5000/api/suggestions?q=${query}`)
        .then((response) =>  response.json())
        .then((data) => {
          setResults(
            data.map((suggestion) => ({
              ...suggestion,
              inHistory: history.includes(suggestion.query),
            }))
          );
        })
        .catch((error) => {
          console.error(error);
        });
    } else {
      setResults([]);
      setOpen(false);
    }
  }, [query])

  return (
    <div className="search-container">
       <input ref={inputRef} className={open ? 'search-input no-border' : 'search-input'} placeholder="Search..." type="text" value={query} onChange={handleChange} />
       {open && (<ul className="autocomplete-suggestions">
        {results.map((result) => (
          <li className={`autocomplete-suggestion ${result.inHistory ? 'in-history' : ''}`}  key={result._id} onClick={() => handleSelect(result.query)}>
            <span className='autocomplete-suggestion-text'>
              {result.query}</span></li>
        ))}
      </ul>
       )}
    </div>
  );
}

export default AutocompleteSearch;
