import React, { useState, useEffect } from 'react';

function AutocompleteSearch() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleChange = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    if (query.length > 0) {
      // retrieve suggestions from API
      fetch(`http://localhost:5000/api/suggestions?q=${query}`)
        .then((response) =>  response.json())
        .then((data) => {
          setResults(data);
        })
        .catch((error) => {
          console.error(error);
        });
    } else {
      setResults([]);
    }
  }, [query])

  return (
    <div className="search-container">
       <input className="search-input" placeholder="Search..." type="text" value={query} onChange={handleChange} />
       <ul className="autocomplete-suggestions">
        {results.map((result) => (
          <li className="autocomplete-suggestion" key={result._id}>{result.query}</li>
        ))}
      </ul>
    </div>
  );
}

export default AutocompleteSearch;
